sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

// Construct a GlideRecord query to find all NeedIt Task records with Due date
	// field values in the past for the same Assigned to. The records cannot be in the
	// Closed state.
	var gdtNow = new GlideDateTime();	
	var grOverdueNITasks = new GlideRecord('x_58872_needit_needit_task');
	grOverdueNITasks.addQuery('due_date', "<=", gdtNow);
	grOverdueNITasks.addQuery('state', "<", 3);
	grOverdueNITasks.addQuery('assigned_to', current.assigned_to);
	grOverdueNITasks.query();

	// Print to the Message field on a notification.
	var count = grOverdueNITasks.getRowCount();
	if (count > 1) {
		template.print("<font face = 'Tahoma' size = '3'>");
        template.print("In addition to this overdue NeedIt Task, you have " + (grOverdueNITasks.getRowCount()-1) + " more overdue NeedIt Tasks.");
		template.print("<br>Please complete these tasks as soon as possible.</br>");	
		template.print("</font></br>");
	}
	else {
        template.print("Please complete this task as soon as possible.");		
	}

})(current, template, email, email_action, event);
